import { useRef, useState, useEffect } from "react";
import { signup, login, logout, useAuth, db } from "./firebase";
import { collection, getDocs, addDoc, updateDoc, deleteDoc, doc} from "firebase/firestore";
import { deleteUser } from "firebase/auth";

export default function App() {
  const [loading, setLoading] = useState(false);
  const currentUser = useAuth();
  const emailRef = useRef();
  const passwordRef = useRef();
  const [newName, setNewName] = useState("");
  const [newAge, setNewAge] = useState("");
  const [users, setUsers] = useState([]);
  const usersCollectionRef = collection(db, "users");
  const [forum, setForum] = useState([]);
  const [title, setTitle] = useState("");
  const [contents, setContents] = useState("");
  const [link, setLink] = useState("");
  const [author, setAuthor] = useState("");
  const forumColletionRef = collection(db, "forum");

  async function createUser() {
    setLoading(true);
    await addDoc(usersCollectionRef, {name: newName, age: Number(newAge)});
    setLoading(false);
  };
  // const updateUser = async (id, age) => {
  //   const userDoc = doc (db, "users", id);
  //   const newFields = {age: age + 1}
  //   await updateDoc(userDoc, newFields);
  // }
  const deleteUser = async (id) => {
    const userDoc = doc (db, "users", id);
    await deleteDoc(userDoc);
  }

  const getUsers = async () => {
    const data = await getDocs (usersCollectionRef);
    setUsers(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
  }
  getUsers();

  async function handleSignup() {
    setLoading(true);
    await signup(emailRef.current.value, passwordRef.current.value);
    setLoading(false);
  }

  async function handleLogin() {
    setLoading(true);
    try {
      await login(emailRef.current.value, passwordRef.current.value);
    } catch {
      alert("Error!");
    }
    setLoading(false);
  }

  async function handleLogout() {
    setLoading(true);
    try {
      await logout();
    } catch {
      alert("Error!");
    }
    setLoading(false);
  }

  const ColoredLine = ({ color }) => (
    <hr
        style={{
            color: color,
            backgroundColor: color,
            height: 1
        }}
    />
  );
  return (
    <div className="App">
      <div id="login">
        <div>Currently logged in as: { currentUser?.email } </div>

        <div id="fields">
          <div>
            <input ref={emailRef} placeholder="Email" />
          </div>
          <div>
            <input ref={passwordRef} type="password" placeholder="Password" />
          </div>
        </div>

        <button disabled={ loading || currentUser } onClick={handleSignup}>Sign Up</button>
        <button disabled={ loading || currentUser } onClick={handleLogin}>Log In</button>
        <button disabled={ loading || !currentUser } onClick={handleLogout}>Log Out</button>

      </div>
      <ColoredLine color = "gray"/>
      {currentUser? 
        <div id="todo">
          <h3>Hi, { currentUser?.email } </h3>
          <div>
            Workout List
          </div>
          <input placeholder="Workout" onChange= {(event) => {
              setNewName(event.target.value);
              }}
            />
            <input type="number" placeholder="Reps" onChange= {(event) => {
              setNewAge(event.target.value);
              }}
            />
            <button onClick ={createUser}> Create </button>
            {users.map((user) =>{
              return(
                <div>
                  <input type="checkbox"/>
                  Workout: {user.name} {" "}
                  Reps: {user.age} {" "}
                  {/* <button onClick = {() => {
                    updateUser(user.id, user.age);
                    }}
                  > 
                  Change Age 
                  </button> */}
                  <button onClick = {() => {deleteUser(user.id)}}>Delete</button>
                  {" "}
                </div>  
              );
            })}
            {" "}
        </div>
      : ' ' }
      <ColoredLine color = "gray"/>
      <div id="todo">
          <h3>Workout Forum</h3>
          <div>
            Workout List
          </div>
          <input placeholder="Workout" onChange= {(event) => {
              setNewName(event.target.value);
              }}
            />
            <input type="number" placeholder="Reps" onChange= {(event) => {
              setNewAge(event.target.value);
              }}
            />
            <button onClick ={createUser}> Create </button>
            {users.map((user) =>{
              return(
                <div>
                  <input type="checkbox"/>
                  Workout: {user.name} {" "}
                  Reps: {user.age} {" "}
                  {/* <button onClick = {() => {
                    updateUser(user.id, user.age);
                    }}
                  > 
                  Change Age 
                  </button> */}
                  <button onClick = {() => {deleteUser(user.id)}}>Delete</button>
                  {" "}
                </div>  
              );
            })}
            {" "}
        </div>
    </div>
  );
}
