import { useRef, useState, useEffect } from "react";
import { signup, login, logout, useAuth, db } from "./firebase";
import { collection, getDocs, addDoc, updateDoc, deleteDoc, doc} from "firebase/firestore";
import { deleteUser } from "firebase/auth";

export default function App() {
  const [loading, setLoading] = useState(false);
  const currentUser = useAuth();
  const emailRef = useRef();
  const passwordRef = useRef();
  const [newName, setNewName] = useState("");
  const [newAge, setNewAge] = useState("");
  const [users, setUsers] = useState([]);
  const usersCollectionRef = collection(db, "users");
  const [forum, setForum] = useState([]);
  const [title, setTitle] = useState("");
  const [contents, setContents] = useState("");
  const [link, setLink] = useState("");
  const [author, setAuthor] = useState("");
  const forumColletionRef = collection(db, "forum");

  async function createPlan() {
    setLoading(true);
    await addDoc(forumColletionRef, {author: author, title:title, contents: contents, link:link, vote: 0});
    setLoading(false);
  };
  const deletePost = async (id) => {
    const userDoc = doc (db, "forum", id);
    await deleteDoc(userDoc);
  }
  const addVote = async (id, vote) => {
    const userDoc = doc (db, "forum", id);
    const newFields = {vote: vote + 1}
    await updateDoc(userDoc, newFields);
  }
  const subtractVote = async (id, vote) => {
    const userDoc = doc (db, "forum", id);
    const newFields = {vote: vote - 1}
    await updateDoc(userDoc, newFields);
  }
  const getForum = async () => {
    const data = await getDocs (forumColletionRef);
    setForum(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
  }
  getForum();

  async function createUser() {
    setLoading(true);
    await addDoc(usersCollectionRef, {name: newName, age: Number(newAge)});
    setLoading(false);
  };
  const deleteUser = async (id) => {
    const userDoc = doc (db, "users", id);
    await deleteDoc(userDoc);
  }

  const getUsers = async () => {
    const data = await getDocs (usersCollectionRef);
    setUsers(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
  }
  getUsers();

  async function handleSignup() {
    setLoading(true);
    await signup(emailRef.current.value, passwordRef.current.value);
    setLoading(false);
  }

  async function handleLogin() {
    setLoading(true);
    try {
      await login(emailRef.current.value, passwordRef.current.value);
    } catch {
      alert("Error!");
    }
    setLoading(false);
  }

  async function handleLogout() {
    setLoading(true);
    try {
      await logout();
    } catch {
      alert("Error!");
    }
    setLoading(false);
  }

  const Line = ({ color }) => (
    <hr
        style={{
            color: color,
            backgroundColor: color,
            height: 1
        }}
    />
  );
  return (
    <div className="App">
      <div id="login">
        Currently logged in as: { currentUser?.email } {" "}
        {currentUser? 
          <button onClick={handleLogout}>
          Log Out
          </button>
        : 
          <div>
            <div id="fields">
              <div>
                <input ref={emailRef} placeholder="Email" />
              </div>
              <div>
                <input ref={passwordRef} type="password" placeholder="Password" />
              </div>
            </div>
            <button onClick={handleSignup}>
              Sign Up
            </button>
            <button onClick={handleLogin}>
              Log In
            </button>
          </div>  
        }
      </div>
      {currentUser? 
        <div id="todo">
          <h3>Hi, { currentUser?.email } </h3>
          <div>
            Workout List
          </div>
          <input placeholder="Workout" onChange= {(event) => {
              setNewName(event.target.value);
              }}
            />
            <input type="number" placeholder="Reps" onChange= {(event) => {
              setNewAge(event.target.value);
              }}
            />
            <button onClick ={createUser}> Create </button>
            {users.map((user) =>{
              return(
                <div>
                  <input type="checkbox"/>
                  Workout: {user.name},
                  Reps: {user.age} {" "}
                  <button onClick = {() => {deleteUser(user.id)}}>Delete</button>
                  {" "}
                </div>  
              );
            })}
            {" "}
            <Line color = "gray"/>
            <div>
              Workout Plan
              <table>
        <tr>
          <th>Name</th>
          <th>Age</th>
          <th>Gender</th>
        </tr>
        <tr>
          <td>Anom</td>
          <td>19</td>
          <td>Male</td>
        </tr>
        <tr>
          <td>Megha</td>
          <td>19</td>
          <td>Female</td>
        </tr>
        <tr>
          <td>Subham</td>
          <td>25</td>
          <td>Male</td>
        </tr>
      </table>
            </div>
        </div>
      : <h3>Login for todo list</h3> }
      <Line color = "gray"/>
      <div id="todo">
          <h3>Workout Forum</h3>
          {currentUser? 
          <div>
            <input placeholder="Author Name" onChange= {(event) => {
              setAuthor(event.target.value);
              }}
            />
            <input placeholder="Title" onChange= {(event) => {
              setTitle(event.target.value);
              }}
            />
            <div>
              <input placeholder="Contents" onChange= {(event) => {
                setContents(event.target.value);
                }}
              />
              <input placeholder="Link" value= "www." onChange= {(event) => {
                setLink(event.target.value);
                }}
              />
            </div>
              <button onClick ={createPlan}> Post </button>
            </div>
            : <div>Login to post!</div>}
            {forum.map((user) =>{
              return(
                <div>
                  <div>
                    Author: {user.author} {" "}
                  </div>
                  <div>
                    Title: {user.title} {" "}
                  </div>
                  <div>
                    Contents: {user.contents} {" "}
                  </div>
                  <div>
                    Link: <a href={ 'https://' + user.link }>{user.link}</a> {" "}
                  </div>
                  <div>
                    Vote: 
                    <button onClick = {() => {
                    subtractVote(user.id, user.vote);
                    }}
                    > 
                    &#8595;
                    </button>
                    {user.vote}
                    <button onClick = {() => {
                    addVote(user.id, user.vote);
                    }}
                    > 
                    &#8593;
                    </button>
                   {" "}
                  </div>
                  <button onClick = {() => {deletePost(user.id)}}>Delete Post </button>
                  {" "}
                </div>  
              );
            })}
            {" "}
        </div>
    </div>
  );
}
