import { useEffect, useState } from "react";

// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged, signOut } from "firebase/auth";

const firebaseConfig = {
  apiKey: "AIzaSyCW-z5IlOkyUVQFUORZ0k6F5P8zK19jYrk",
  authDomain: "fitness-tracker-be806.firebaseapp.com",
  projectId: "fitness-tracker-be806",
  storageBucket: "fitness-tracker-be806.appspot.com",
  messagingSenderId: "351303724537",
  appId: "1:351303724537:web:85da640a8c191946df6128",
  measurementId: "G-G2MG85G5VR"
};

const app = initializeApp(firebaseConfig);
const auth = getAuth();

export function signup(email, password) {
  return createUserWithEmailAndPassword(auth, email, password);
}

export function login(email, password) {
  return signInWithEmailAndPassword(auth, email, password);
}

export function logout() {
  return signOut(auth);
}

export function useAuth() {
  const [ currentUser, setCurrentUser ] = useState();

  useEffect(() => {
    const unsub = onAuthStateChanged(auth, user => setCurrentUser(user));
    return unsub;
  }, [])

  return currentUser;
}
